let getHandler = async () => ({
  body: '',
  statusCode: 200,
  headers: {},
});

const instance = {
  get: (...args) => getHandler(...args),
  extend: () => instance,
  __setGetHandler: (handler) => {
    getHandler = handler;
  },
};

const got = Object.assign(
  (...args) => getHandler(...args),
  instance,
  {
    extend: () => instance,
    __setGetHandler: (handler) => {
      getHandler = handler;
    },
  },
);

module.exports = got;
module.exports.default = got;
